const { PDFDocument } = require('pdf-lib'); // librería incluida en n8n o puedes instalarla en tu instancia

async function crearPDF() {
    const pdfDoc = await PDFDocument.create();
    const page = pdfDoc.addPage([595, 842]);

    // Tomar los datos recibidos del Webhook
    const { sistolica, diastolica, edad, recomendacion } = items[0].json;
    const fecha = new Date().toLocaleString();

    page.drawText(`Reporte de Presión Arterial`, { x: 160, y: 800, size: 18 });
    page.drawText(`Fecha: ${fecha}`, { x: 40, y: 760, size: 14 });
    page.drawText(`Edad: ${edad}`, { x: 40, y: 740, size: 14 });
    page.drawText(`Sistólica: ${sistolica}`, { x: 40, y: 720, size: 14 });
    page.drawText(`Diastólica: ${diastolica}`, { x: 40, y: 700, size: 14 });
    page.drawText(`Recomendación: ${recomendacion}`, { x: 40, y: 680, size: 14 });

    const pdfBytes = await pdfDoc.save();


    // Devuelve el PDF como base64
    return [{ json: { pdfBase64: Buffer.from(pdfBytes).toString('base64') } }];
}

return crearPDF();
